import React, { useState, useEffect } from "react";

const SimpleInput = (props) => {
  const [inputValue, setInputValue] = useState("");
  // const inputRef = useRef();
  // const [nameIsValid, setEnteredNameIsValid] = useState(false);
  const [enteredNameTouched, setEnteredNameTouched] = useState(false);
  // const [formIsValid,setFormIsValid]  = useState(false);


  const enteredNameIsValid = inputValue.trim()!== '';
  // const enteredAgeIsValid = inputValue.trim()!== '';
  // const enteredDobIsValid = inputValue.trim()!== '';

  let formIsValid = false;

  if(enteredNameIsValid){
    formIsValid = true;
  }

  // useEffect(()=>{
  //   if(enteredNameIsValid){
  //     setFormIsValid(true);
  //   }else{
  //     setFormIsValid(false);
  //   }

  // },[enteredNameIsValid]);

  // useEffect(() => {
  //   if (nameIsValid) {
  //     //call api
  //   }
  // }, [nameIsValid]);

  const formSubmitHandler = (event) => {
    event.preventDefault();
    // console.log(event.target.value);
    setEnteredNameTouched(true);


    if(!enteredNameIsValid){
      return;
    }
    // setEnteredNameIsValid(true);
    console.log(inputValue);

    const enteredValue = inputRef.current.value;
    console.log(enteredValue);
    setInputValue('');
    setEnteredNameTouched(false);
  };

  const nameInputBlurHandler = () =>{
     setEnteredNameTouched(true);

  }

  const inputChangeHandler = (event) => {
    console.log(event.target.value);
    setInputValue(event.target.value);

  };

  console.log(enteredNameTouched);

  const nameInputIsValid = (!enteredNameIsValid && enteredNameTouched)
    ? "form-control invalid"
    : "form-control";

  return (
    <form onSubmit={formSubmitHandler}>
      <div className={nameInputIsValid}>
        <label htmlFor="name">Your Name</label>
        <input
          ref={inputRef}
          type="text"
          id="name"
          onBlur={nameInputBlurHandler}
          value={inputValue}
          onChange={inputChangeHandler}
        />
        {!enteredNameIsValid && enteredNameTouched && (
          <p className="error-text">Name must not be empty.</p>
        )}
      </div>
      <div className="form-actions">
        <button disabled ={!formIsValid}>Submit</button>
      </div>
    </form>
  );
};

export default SimpleInput;
