import React, { useState } from "react";
import useInput from "../hooks/user-input";


const SimpleInput = (props) => {

  const { value,
    isTouched,
    enteredValueIsValid,
    valueChangeHandler,
    valueBlurHandler,
    reset} = useInput(value => value.trim() !== '');

    const pattern = /[a-zA-Z0-9]+[\.]?([a-zA-Z0-9]+)?[\@][a-z]{3,9}[\.][a-z]{2,5}/g;
    const result = pattern.test(emailInputValue.trim());
    const enteredEmailIsValid = emailInputValue.trim()!=='' && result;


    const { emailValue,
      isEmailTouched,
      enteredEmailValueIsValid,
      emailChangeHandler,
      emailBlurHandler,
      emailReset} = useInput(emailValue => value.trim() !== '');

  // const [emailInputValue, setEmailInputValue] = useState("");
  // const [enteredEmailTouched, setEnteredEmailTouched] = useState(false);
  // const enteredNameIsValid = inputValue.trim()!== '';

  let formIsValid = false;

  if(enteredValueIsValid && enteredEmailIsValid){
    formIsValid = true;
  }



  const formSubmitHandler = (event) => {
    event.preventDefault();

    setEnteredEmailTouched(true);

    if(!enteredValueIsValid || !enteredEmailIsValid){
      return;
    }

    reset();
    setEmailInputValue('');
    setEnteredEmailTouched(false);
  };



  const emailInputBlurHandler = () =>{
    setEnteredEmailTouched(true);
 }



  const emaiInputChangeHandler = (event) => {
    console.log(event.target.value);
    setEmailInputValue(event.target.value);
  };


  const nameInputIsValid = (!enteredValueIsValid && isTouched)
    ? "form-control invalid"
    : "form-control";

    const emailInputIsValid = (!enteredEmailIsValid && enteredEmailTouched)
    ? "form-control invalid"
    : "form-control";

  return (
    <form onSubmit={formSubmitHandler}>
      <div className={nameInputIsValid}>
        <label htmlFor="name">Your Name</label>
        <input
          type="text"
          id="name"
          placeholder="Name"
          onBlur={valueBlurHandler}
          value={value}
          onChange={valueChangeHandler}
        />
        {!enteredValueIsValid && isTouched && (
          <p className="error-text">Name must not be empty.</p>
        )}
      </div>
      <div className={emailInputIsValid}>
        <label htmlFor="email">Your Email</label>
        <input
          type="text"
          id="email"
          size="30"
          placeholder="Email"
          onBlur={emailInputBlurHandler}
          value={emailInputValue}
          onChange={emaiInputChangeHandler}
        />
        {!enteredEmailIsValid && enteredEmailTouched && (
          <p className="error-text">Email must not be empty.</p>
        )}
      </div>
      <div className="form-actions">
        <button disabled ={!formIsValid}>Submit</button>
      </div>
    </form>
  );
};

export default SimpleInput;
