import React, { useState } from "react";

const BasicForm = (props) => {
  const [fname, setfName] = useState("");
  const [fnameTouched, setfNameTouched] = useState(false);
  const [lname, setlName] = useState("");
  const [email, setEmail] = useState("");

  const fnameIsvalid = fname.trim() !== "";
  const lnameIsvalid = lname.trim() !== "";
  const emailIsvalid = email.trim() !== "";

  let formIsValid = false;
  if (fnameIsvalid) {
    formIsValid = true;
  }

  // fname Handlers
  const fnameChangeHandler = (event) => {
    setfName(event.target.value);
  };

  const fnameBlurHandler = () => {
    setfNameTouched(true);
  };
//lname Handlers

const lnameChangeHandler = (event) => {
  setfName(event.target.value);
};


  const submitHandler = (event) => {
    event.preventDefault();
    if (!fnameIsvalid && fnameTouched) {
      return;
    }

    setfNameTouched(false);
    setfName("");

  };

  // console.log(fnameIsvalid);
  // console.log(fnameTouched);

  const validationStyleSheet =
    fnameTouched && !fnameIsvalid
      ? "form-control invalid"
      : " form-control";

  return (
    <form onSubmit={submitHandler}>
      <div className="control-group">
        <div className={validationStyleSheet}>
          <label htmlFor="name">First Name</label>
          <input
            type="text"
            id="name"
            placeholder="first name"
            value={fname}
            onChange={fnameChangeHandler}
            onBlur={fnameBlurHandler}
          />
          {!fnameIsvalid &&  fnameTouched &&<p className="error-text">First name is invalid</p>}
        </div>
        <div className="form-control">
          <label htmlFor="name">Last Name</label>
          <input type="text" id="name"   placeholder="last name" />
        </div>
      </div>
      <div className="form-control">
        <label htmlFor="name">E-Mail Address</label>
        <input type="text" id="name"   placeholder="email"/>
      </div>
      <div className="form-actions">
        <button disabled={!formIsValid}>Submit</button>
      </div>
    </form>
  );
};

export default BasicForm;
